    getHsCode: function(bidData) {
        var me = this,
            vm = me.getViewModel();

        var hsCodeStr = bidData.get('hsCode');
        var returnHsCode = null;

        if(!!hsCodeStr){
            var hsCodePrefix = hsCodeStr.substring(0, 4);

            // 2016.01.19. KJY.   10 
            if(hsCodePrefix == '9999'){
                returnHsCode = hsCodeStr.substring(4);
            }else{
                returnHsCode = hsCodeStr;
            }
        }

        return returnHsCode;
    },

